mapper.update:
        UPDATE comment c
SET c.read = 'Y'
WHERE boardNum IN (
    SELECT boardNum
    FROM board
    WHERE id = #{id}
    AND boardNum = #{boardNum})
AND id != #{id}
AND submitTime = #{submitTime};

UPDATE board_like b
SET b.read = 'Y'
WHERE boardNum IN (
    SELECT boardNum
    FROM board
    WHERE id = #{id}
    AND boardNum = #{boardNum})
AND id != #{id}
AND submitTime = #{submitTime};
